        if (isShowing)
            return;
        isShowing = true;
        showAnimation();
    }
    private void hide() {
        clearIndicatorsAnimations();
        isShowing = false;
    }
    private void clearIndicatorsAnimations() {
        for (WP10Indicator wp10Indicator : wp10Indicators) {
            wp10Indicator.removeAnim();
        }
    }
